mapper.select:
SELECT `user`.user_id, `user`.username, `user`.`password`, `user`.salt, `user`.phone, `user`.avatar, `user`.create_time AS ucreate_time, `user`.update_time AS uupdate_time, `user`.del_flag AS udel_flag, `user`.lock_flag AS lock_flag, `user`.organization_id AS organizationId, `user`.sex AS sex, `user`.realname AS realname, `user`.email AS email, `user`.remark AS remark, d.full_name AS organizationName, d.is_can_select AS organizationCanSelect FROM sys_user AS `user` LEFT JOIN sys_organization AS d ON d.id = `user`.organization_id
`user`.del_flag = '0'
AND `user`.username LIKE CONCAT('%',#{query.username},'%')
AND `user`.realname LIKE CONCAT('%',#{query.realname},'%')
AND `user`.email LIKE CONCAT('%',#{query.email},'%')
AND `user`.phone LIKE CONCAT('%',#{query.phone},'%')
AND `user`.lock_flag = #{query.lockFlag}
AND `user`.sex = #{query.sex}
AND `user`.organization_id = #{query.organizationId}
AND `user`.user_id IN( SELECT user_id FROM sys_user_role WHERE role_id IN
#{item}
)
AND `user`.user_id IN(SELECT user_id FROM sys_user_role WHERE role_id IN(SELECT role_id FROM sys_role WHERE role_name LIKE CONCAT('%',#{query.roleName},'%')))
AND `user`.organization_id IN(SELECT id FROM sys_organization WHERE name LIKE CONCAT('%',#{query.organizationName},'%'))
AND `user`.organization_id IN(SELECT id FROM sys_organization WHERE parent_id_path LIKE CONCAT('%/',#{userOrganizationId},'/%'))
ORDER BY `user`.create_time DESC
